import { Layout, theme } from "antd";
import AppContent from "./components/AppContent";
import AppSidebar from "./components/AppSidebar";
import AppNavbar from "./components/AppNavbar";
import { useState } from "react";

const App = () => {
  const { Content } = Layout;
  const {
    token: { colorBgContainer },
  } = theme.useToken();
  const [color, setColor] = useState();
  const changeColor = (color) => {
    setColor(color);
  };
  return (
    <div>
      <div>
        <Content
          className="fs-5"
          style={{
            margin: "12px",
            padding: 24,
            height: "88vh",
            background: colorBgContainer,
          }}
        >
          {
            <Card
              bg="success"
              style={{ width: "25rem", color: "white", textAlign: "center" }}
              className="mb-2"
            >
              <Card.Header className="fs-4">
                Change SideBar Color Card
              </Card.Header>
              <Card.Body>
                <Card.Text>
                  <input
                    style={{
                      width: "50px",
                      height: "50px",
                      border: "1px solid red",
                      borderRadius: "50%",
                      background: "red",
                      marginLeft: "10px",
                    }}
                    onClick={changeColor}
                    type="submit"
                    value=""
                  />
                  <input
                    style={{
                      width: "50px",
                      height: "50px",
                      border: "1px solid blue",
                      borderRadius: "50%",
                      background: "blue",
                      marginLeft: "10px",
                    }}
                    onClick={changeColor}
                    type="submit"
                    value=""
                  />
                  <input
                    style={{
                      width: "50px",
                      height: "50px",
                      border: "1px solid green",
                      borderRadius: "50%",
                      background: "green",
                      marginLeft: "10px   ",
                    }}
                    onClick={changeColor}
                    type="submit"
                    value=""
                  />
                  <input
                    style={{
                      width: "50px",
                      height: "50px",
                      border: "1px solid orange",
                      borderRadius: "50%",
                      background: "orange",
                      marginLeft: "10px",
                    }}
                    onClick={changeColor}
                    type="submit"
                    value=""
                  />
                </Card.Text>
              </Card.Body>
            </Card>
          }
        </Content>
      </div>
      <AppNavbar changeColor={changeColor} />
      <Layout>
        <AppSidebar changeColor={changeColor} />
        <Layout>
          <AppContent changeColor={changeColor} />
        </Layout>
      </Layout>
    </div>
  );
};

export default App;
